    ) VALUES (1377396, 'Calcitriol', 'Mineral oil', 'Moderate', 'INTERVAL: Coadministration of mineral oil and oral vitamin D (a fat soluble vitamin) may lead to decreased effectiveness of vitamin D. Mineral oil most likely decreases gastrointestinal absorption of vitamin D.', NULL, 'Consider either separating the doses by at least 3 hours, substituting mineral oil with alternative therapy or using parenteral vitamin D.', NULL, 'Consider either separating the doses by at least 3 hours, substituting mineral oil with alternative therapy or using parenteral vitamin D.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
