        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1377555, 'Diclofenac', 'Colestipol', 'Minor', 'The concomitant administration of colestipol with diclofenac results in a significant reduction in diclofenac''s bioavailability. The clinical significance of this reduction is not clear. Separating the time of administration between colestipol and diclofenac may diminish the effects of this interaction.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
