    ) VALUES (1377564, 'Diclofenac', 'Deferiprone', 'Moderate', 'Coadministration of deferiprone with uridine diphosphate glucuronosyltransferase (UGT) 1A6 inhibitors may increase the plasma concentrations of deferiprone, which is metabolized by the enzyme.', NULL, 'The use of deferiprone with UGT1A6 inhibitors should be avoided.', NULL, 'The use of deferiprone with UGT1A6 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
