    ) VALUES (1377662, 'Diclofenac', 'Linaclotide', 'Minor', 'The concomitant use of linaclotide with proton pump inhibitors, laxatives, or nonsteroidal anti-inflammatory drugs may increase the risk of diarrhea.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
