    ) VALUES (1377678, 'Diclofenac', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of diclofenac, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if diclofenac is prescribed in combination with an inhibitor of CYP450 2C9.', NULL, 'Caution is advised if diclofenac is prescribed in combination with an inhibitor of CYP450 2C9.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
