        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1377807, 'Diclofenac', 'Valdecoxib', 'Moderate', 'Concomitant use of COX-2 inhibitors with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation. The risk is dependent on both dosage and duration of therapy.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided. Some authorities consider the use of COX-2 inhibitors with non-aspirin NSAIDs as contraindicated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
