    ) VALUES (1377821, 'Canagliflozin', 'Acetohexamide', 'Moderate', 'Coadministration of a sodium-glucose co-transporter 2 (SGLT-2) inhibitor with an insulin secretagogue (e.g., sulfonylurea, meglitinide) or insulin may potentiate the risk of hypoglycemia.', NULL, 'A lower dosage of the insulin secretagogue or insulin may be required when used in combination with an SGLT-2 inhibitor.', NULL, 'A lower dosage of the insulin secretagogue or insulin may be required when used in combination with an SGLT-2 inhibitor.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
