INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (90459, 'Dopamine', 'Milnacipran', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90561/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[12] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (90460, 'Dopamine', 'Molindone', 'Moderate', 'Phenothiazines and other neuroleptics may inhibit or reverse the pressor effect of adrenaline (epinephrine), dopamine, and similar vasoconstrictors. Many of these agents, including the atypical antipsychotics, exhibit alpha-1 adrenergic blocking activity and produce hypotension as an adverse effect. Use of adrenaline or dopamine for drug-induced hypotension and circulatory collapse in patients receiving neuroleptic therapy may cause a paradoxical further lowering of blood pressure, since beta stimulation may worsen hypotension in the setting of alpha blockade.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents. Alternative vasopressor agents such as metaraminol, noradrenaline (norepinephrine), or phenylephrine should be considered, and vital signs closely monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90562/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Loxitane C Oral Concentrate (loxapine)" Watson Laboratories Inc, Corona, CA.[4] Sletten IW, Lang WJ, Brown ML, Ballou SR, Gershon S "Chronic chlorpromazine administration: some pharmacological and psychological effects in man." Clin Pharmacol Ther 6 (1965): 575-86[5] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[6] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[7] Ginsburg J, Duff RS "Effect of chlorpromazine on adrenaline vasoconstriction in man." Br J Pharmacol 11 (1956): 180-5[8] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[9] "Product Information. Thorazine (chlorpromazine)." SmithKline Beecham, Philadelphia, PA.[10] "Product Information. Moban (molindone)." Gate Pharmaceuticals, Sellersville, PA.[11] "Product Information. Navane (thiothixene)." Roerig Division, New York, NY.[12] "Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[13] Foster CA, O''Mullane EJ, Gaskell P, Churchill-Davidson HC "Chlorpromazine: a study of its action on the circulation in man." Lancet 2 (1954): 614-7[14] Lear E, Chiron AE, Pallin IM "A clinical study of mechanisms of action of chlorpromazine." JAMA 163 (1957): 30-6[15] Gonzalez ER "Catecholamine selection for vasopressor-dependent patients." Clin Pharm 7 (1988): 493, 496[16] "Product Information. Torecan (thiethylperazine)" Roxane Laboratories Inc, Columbus, OH.[17] Goulet JP, Perusse R, Turcotte JY "Contraindications to vasoconstrictors in dentistry: Part III. Pharmacologic interactions." Oral Surg Oral Med Oral Pathol 74 (1992): 692-7[18] "Product Information. Invega (paliperidone)." Janssen Pharmaceuticals, Titusville, NJ.[19] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[20] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[21] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[22] "Product Information. Re