        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1378057, 'Canagliflozin', 'Sibutramine', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', NULL, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
