INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378358, 'Cangrelor', 'Bromfenac', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 1767317475, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378359, 'Caplacizumab', 'Cangrelor', 'Major', 'Concomitant use of caplacizumab with other agents that affect hemostasis such as anticoagulants, platelet aggregation inhibitors, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding.', 'DDInter', 1767317475, 'Caution is recommended if caplacizumab must be used with other agents that affect hemostasis. Patients should be monitored for potential bleeding complications and therapy interrupted if clinically significant bleeding occurs. If needed, von Willebrand factor concentrate may be administered to rapidly correct hemostasis.', 'Synergism', 'Caution is recommended if caplacizumab must be used with other agents that affect hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378360, 'Capsicum', 'Cangrelor', 'Minor', 'Theoretically, capsicum may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Animal and limited human data suggest that capsicum may increase fibrinolytic activity, lower plasma fibrinogen levels, increase plasma antithrombin III levels, inhibit platelet aggregation, and prolong bleeding time. In patients who have used capsicum extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378361, 'Chamomile', 'Cangrelor', 'Minor', 'Some herbs such as chamomile and fenugreek have coumarin constituents. Theoretically, ingesting large quantities of these herbs may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. However, their effects on the coagulation system have not been studied, and bleeding complications have not been reported in humans. Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used chamomile or fenugreek extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378362, 'Cilostazol', 'Cangrelor', 'Moderate', 'Coadministration of cilostazol with other antiplatelet agents may produce additive pharmacodynamic effects resulting in increased inhibition of platelet function.', 'DDInter', 1767317475, 'Because of theoretical concerns regarding increased inhibition of platelet aggregation, cilostazol should be used cautiously with other antiplatelet agents.', 'Synergism', 'Because of theoretical concerns regarding increased inhibition of platelet aggregation, cilostazol should be used cautiously with other antiplatelet agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378363, 'Citalopram', 'Cangrelor', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 1767317475, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378364, 'Clomipramine', 'Cangrelor', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 1767317475, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378365, 'Clopidogrel', 'Cangrelor', 'Major', 'INTERVAL: The antiplatelet effect of clopidogrel or prasugrel may be inhibited when they are administered at the same time as cangrelor. The mechanism is competitive inhibition of P2Y12 platelet receptors by cangrelor, which, unlike clopidogrel or prasugrel, binds reversibly at the receptor site and causes a temporary rather than persistent inhibition of ADP-induced platelet activation and aggregation. According to the product labeling for cangrelor, the expected antiplatelet effect of a 600 mg loading dose of clopidogrel or a 60 mg loading dose of prasugrel was blocked when these agents were administered during a cangrelor infusion. Conversely, pretreatment with clopidogrel or prasugrel does not impair the antiplatelet effect of cangrelor, but may result in additive platelet inhibition.', 'DDInter', 1767317475, 'To maintain platelet inhibition, clopidogrel 600 mg or prasugrel 60 mg should be administered immediately after discontinuation of the cangrelor infusion. Do not administer prior to discontinuation of cangrelor, as clopidogrel and prasugrel will have no antiplatelet effect until the next dose is administered. Alternatively, ticagrelor 180 mg may be administered at any time during cangrelor infusion or immediately after discontinuation.', 'Antagonism', 'To maintain platelet inhibition, clopidogrel 600 mg or prasugrel 60 mg should be administered immediately after discontinuation of the cangrelor infusion.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378366, 'Clove', 'Cangrelor', 'Minor', 'Clove and clove oil may potentiate the effects of anticoagulants and other drugs that may affect hemostasis such as platelet inhibitors, thrombolytic agents, and nonsteroidal anti-inflammatory drugs, possibly increasing the risk of bleeding. Eugenol, the active constituent of clove and clove oil, has been shown to be an inhibitor of platelet activity.', 'DDInter', 1767317475, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used clove and clove oil extensively prior to receiving treatment with anticoagulants or other drugs that may affect hemostasis, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378367, 'Collagenase clostridium histolyticum', 'Cangrelor', 'Moderate', 'The safety and efficacy of collagenase clostridium histolyticum in patients currently receiving, or within 7 days of receiving, anticoagulant medications other than low-dose aspirin (e.g., up to 150 mg/day) is unknown. Theoretically, the risk of bleeding complications may be increased.', 'DDInter', 1767317475, 'Collagenase clostridium histolyticum should be used with caution in patients receiving or within 7 days of receiving anticoagulants (except for low-dose aspirin), platelet inhibitors, or thrombolytic agents.', 'Synergism', 'Collagenase clostridium histolyticum should be used with caution in patients receiving or within 7 days of receiving anticoagulants (except for low-dose aspirin), platelet inhibitors, or thrombolytic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
