INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378418, 'Cangrelor', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 1767317475, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, NULL, 'Major', 'pharmacodynamic');
