INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378427, 'Cangrelor', 'Ponatinib', 'Major', 'Coadministration of ponatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with ponatinib is associated with severe, sometimes fatal hemorrhage including cerebral and gastrointestinal hemorrhage.', 'DDInter', 1767317475, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378428, 'Cangrelor', 'Prasugrel', 'Major', 'INTERVAL: The antiplatelet effect of clopidogrel or prasugrel may be inhibited when they are administered at the same time as cangrelor. The mechanism is competitive inhibition of P2Y12 platelet receptors by cangrelor, which, unlike clopidogrel or prasugrel, binds reversibly at the receptor site and causes a temporary rather than persistent inhibition of ADP-induced platelet activation and aggregation. According to the product labeling for cangrelor, the expected antiplatelet effect of a 600 mg loading dose of clopidogrel or a 60 mg loading dose of prasugrel was blocked when these agents were administered during a cangrelor infusion. Conversely, pretreatment with clopidogrel or prasugrel does not impair the antiplatelet effect of cangrelor, but may result in additive platelet inhibition.', 'DDInter', 1767317475, 'To maintain platelet inhibition, clopidogrel 600 mg or prasugrel 60 mg should be administered immediately after discontinuation of the cangrelor infusion. Do not administer prior to discontinuation of cangrelor, as clopidogrel and prasugrel will have no antiplatelet effect until the next dose is administered. Alternatively, ticagrelor 180 mg may be administered at any time during cangrelor infusion or immediately after discontinuation.', 'Antagonism', 'To maintain platelet inhibition, clopidogrel 600 mg or prasugrel 60 mg should be administered immediately after discontinuation of the cangrelor infusion.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378429, 'Cangrelor', 'Ramucirumab', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', 'DDInter', 1767317475, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378430, 'Cangrelor', 'Regorafenib', 'Major', 'Coadministration of regorafenib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with regorafenib is associated with a high frequency of hemorrhage and thrombocytopenia. Coadministration of regorafenib with strong UGT1A9 inhibitors such as mefenamic acid or diflunisal may theoretically increase systemic exposure to regorafenib.', 'DDInter', 1767317475, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378431, 'Cangrelor', 'Reteplase', 'Major', 'Concomitant use of cangrelor with anticoagulants, thrombolytic agents, or other antiplatelet agents may theoretically increase the risk of bleeding.', 'DDInter', 1767317475, 'Caution is recommended if cangrelor must be used in patients treated with other agents that alter hemostasis. Patients should be monitored for potentially increased bleeding complications.', 'Synergism', 'Caution is recommended if cangrelor must be used in patients treated with other agents that alter hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
