INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378459, 'Cangrelor', 'Zanubrutinib', 'Major', 'Coadministration of zanubrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317475, 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Discontinue zanubrutinib if intracranial hemorrhage of any grade occurs.', 'Synergism', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378460, 'Aminophylline', 'Cannabidiol', 'Moderate', 'Concomitant use of cannabis (marijuana) may decrease the plasma concentrations of theophylline. Smoking marijuana has been reported to increase theophylline clearance, with effect similar to that of smoking tobacco. The mechanism of interaction has not been established, but may involve induction of CYP450 1A2-mediated metabolism by the cannabinoids in marijuana. In vitro data suggest that delta-9-tetrahydrocannabinol may induce CYP450 1A2, while cannabidiol may induce or inhibit CYP450 1A2 at clinically relevant concentrations.', 'DDInter', 1767317475, 'Theophylline levels should be monitored closely in patients who use marijuana or its cannabinoids. Dosage adjustments may be required following initiation, discontinuation, or change of cannabis/cannabinoid use.', 'Metabolism', 'Theophylline levels should be monitored closely in patients who use marijuana or its cannabinoids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378461, 'Amoxapine', 'Cannabidiol', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
