INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378567, 'Everolimus', 'Cannabidiol', 'Moderate', 'Concomitant use of cannabidiol with calcineurin or mammalian target of rapamycin (mTOR) inhibitors may increase the serum levels of the calcineurin or mTOR inhibitor and the risk of adverse effects. The mechanism for this interaction is not clearly understood.', 'DDInter', 1767317475, 'Although the interaction has not been studied, caution is advised if cannabidiol is coadministered with calcineurin or mTOR inhibitors. Clinical and laboratory monitoring should be considered whenever cannabidiol is added to or withdrawn from therapy with a calcineurin or mTOR inhibitor, and the dosage of the calcineurin or mTOR inhibitor adjusted as necessary. Patients should be monitored for development of adverse effects associated with the calcineurin or mTOR inhibitor.', 'Others', 'Although the interaction has not been studied, caution is advised if cannabidiol is coadministered with calcineurin or mTOR inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
