INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182506, 'Idelalisib', 'Flibanserin', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', 'Ritodrine, Bremelanotide, Cabergoline', 'Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Brigatinib, Encorafenib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182720/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767369485);
