INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (182802, 'Haloperidol', 'Fluconazole', 'Major', 'Haloperidol can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Haloperidol treatment alone has been associated with a number of reported cases of torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with haloperidol. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', NULL, 'Caution is recommended if haloperidol is used in combination with other drugs that can prolong the QT interval, particularly when administered intravenously or at higher than recommended dosages.', NULL, 'Caution is recommended if haloperidol is used in combination with other drugs that can prolong the QT interval, particularly when administered intravenously or at higher than recommended dosages.', 'Synergism', 'Tinidazole, Tetracycline, Secnidazole, Cefixime, Amikacin, Micafungin, Caspofungin, Miconazole, Oteseconazole, Flucytosine, Anidulafungin, More', 'Loxapine, Molindone, Thiothixene', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183016/', '[1] Huyse F, van Schijndel RS "Haloperidol and card