INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378804, 'Cannabidiol', 'Sirolimus', 'Moderate', 'Concomitant use of cannabidiol with calcineurin or mammalian target of rapamycin (mTOR) inhibitors may increase the serum levels of the calcineurin or mTOR inhibitor and the risk of adverse effects. The mechanism for this interaction is not clearly understood.', 'DDInter', 1767317475, 'Although the interaction has not been studied, caution is advised if cannabidiol is coadministered with calcineurin or mTOR inhibitors. Clinical and laboratory monitoring should be considered whenever cannabidiol is added to or withdrawn from therapy with a calcineurin or mTOR inhibitor, and the dosage of the calcineurin or mTOR inhibitor adjusted as necessary. Patients should be monitored for development of adverse effects associated with the calcineurin or mTOR inhibitor.', 'Others', 'Although the interaction has not been studied, caution is advised if cannabidiol is coadministered with calcineurin or mTOR inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378805, 'Cannabidiol', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', 'DDInter', 1767317475, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.', 'Synergism', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
