INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378837, 'Cannabidiol', 'Thalidomide', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', 'DDInter', 1767317475, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1378838, 'Cannabidiol', 'Theophylline', 'Moderate', 'Concomitant use of cannabis (marijuana) may decrease the plasma concentrations of theophylline. Smoking marijuana has been reported to increase theophylline clearance, with effect similar to that of smoking tobacco. The mechanism of interaction has not been established, but may involve induction of CYP450 1A2-mediated metabolism by the cannabinoids in marijuana. In vitro data suggest that delta-9-tetrahydrocannabinol may induce CYP450 1A2, while cannabidiol may induce or inhibit CYP450 1A2 at clinically relevant concentrations.', 'DDInter', 1767317475, 'Theophylline levels should be monitored closely in patients who use marijuana or its cannabinoids. Dosage adjustments may be required following initiation, discontinuation, or change of cannabis/cannabinoid use.', 'Metabolism', 'Theophylline levels should be monitored closely in patients who use marijuana or its cannabinoids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
