INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (91615, 'Doxepin (topical)', 'Botulinum toxin type B', 'Moderate', 'Use of anticholinergic drugs after administration of botulinum toxin may potentiate systemic anticholinergic effects such as dry mouth, blurred vision, and urinary disorders. Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive or synergistic anticholinergic effects may occur when these agents are used together.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) are used after administration of botulinum toxin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/91719/', '[1] "Product Information. Botox (onabotulinumtoxin A)." Allergan Inc, Irvine, CA.[2] "Product Information. Myobloc (botulinum toxin type B)" Elan Pharmaceuticals, S. San Francisco, CA.[3] "Product Information. Xeomin (botulinum toxin type A (obsolete) (botulinum toxin type A))." Merz Pharmaceuticals, Greensboro, NC.[4] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc., Santa Barbara, CA.[5] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc, Brisbane, CA.[6] Canadian Pharmacists Association "e-CPS.