INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (91949, 'Doxorubicin', 'Pitolisant', 'Moderate', 'Limited data suggest that pitolisant may produce mild to moderate QT interval prolongation (10 to 13 milliseconds) at doses 3 to 6 times the standard therapeutic dose. Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Concomitant use of pitolisant with other agents associated with QT interval prolongation should generally be avoided.', NULL, 'Concomitant use of pitolisant with other agents associated with QT interval prolongation should generally be avoided. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92053/', '[1] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (91950, 'Doxorubicin', 'Ponatinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with ponatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ponatinib.', NULL, 'Caution is advised when ponatinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when ponatinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ponatinib is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92054/', '[1] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767365205);
