    ) VALUES (1378895, 'Aluminum hydroxide', 'Capecitabine', 'Minor', 'Limited data indicate that aluminum and magnesium containing antacids may cause a small increase in the serum levels of capecitabine and one metabolite. The clinical significance of this interaction is not known.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
