    ) VALUES (1378957, 'Capecitabine', 'Ixabepilone', 'Minor', 'In patients with cancer who received ixabepilone (40 mg/m2) in combination with capecitabine (1000 mg/m2), peak plasma concentration (Cmax) of ixabepilone and capecitabine decreased by 19% and 27%, respectively, compared to administration separately. The interaction is not considered clinically significant given that the combination therapy is supported by efficacy data.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
