    ) VALUES (1379026, 'Capecitabine', 'Trichlormethiazide', 'Moderate', 'Antineoplastic-induced bone marrow suppression may be prolonged with concomitant thiazide administration. The mechanism is unknown. Thiazides have been associated with blood dyscrasias.', NULL, 'Alternative antihypertensive therapy may be advisable.', NULL, 'Alternative antihypertensive therapy may be advisable. Hematological status should be closely monitored in patients receiving this combination.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
