    ) VALUES (1379049, 'Cilostazol', 'Caplacizumab', 'Moderate', 'Coadministration of cilostazol with other antiplatelet agents may produce additive pharmacodynamic effects resulting in increased inhibition of platelet function.', NULL, 'Because of theoretical concerns regarding increased inhibition of platelet aggregation, cilostazol should be used cautiously with other antiplatelet agents.', NULL, 'Because of theoretical concerns regarding increased inhibition of platelet aggregation, cilostazol should be used cautiously with other antiplatelet agents.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
