INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379246, 'Capmatinib', 'Maraviroc', 'Moderate', 'Coadministration with inhibitors of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may increase the plasma concentrations of maraviroc, which is a substrate of both.', 'DDInter', 1767317475, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein. Dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379247, 'Capmatinib', 'Melatonin', 'Minor', 'Concomitant administration of CYP450 1A1 or 1A2 inhibitors such as estrogens or quinolones may increase the serum concentration of melatonin. The clinical significance is unknown.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379248, 'Capmatinib', 'Methylphenobarbital', 'Moderate', 'Coadministration with CYP450 3A4 inducers may decrease the plasma concentrations and anti-tumor activity of capmatinib. The proposed mechanism involves induction of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', 'DDInter', 1767317475, 'Caution is recommended whenever capmatinib is used with a CYP450 3A4 inducer. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inducer is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity.', 'Metabolism', 'Caution is recommended whenever capmatinib is used with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379249, 'Capmatinib', 'Methotrexate', 'Moderate', 'Coadministration with capmatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as digoxin and rosuvastatin. The proposed mechanism is decreased clearance due to capmatinib-mediated inhibition of intestinal P-gp and/or BCRP efflux transport proteins.', 'DDInter', 1767317475, 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided. However, if concomitant use is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever capmatinib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379250, 'Capmatinib', 'Methyl aminolevulinate (topical)', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', 'DDInter', 1767317475, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379251, 'Capmatinib', 'Methylnaltrexone', 'Moderate', 'Coadministration with capmatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of multidrug and toxin extrusion 1 and 2K (MATE1 and MATE2K) proteins, such as methylnaltrexone and tipiracil. The proposed mechanism is decreased clearance due to capmatinib-mediated inhibition of MATE1 and/or MATE2K.', 'DDInter', 1767317475, 'Coadministration of capmatinib with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided. However, if concomitant use is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever capmatinib is added to or withdrawn from therapy with these drugs, and the dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Coadministration of capmatinib with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
