INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379330, 'Capmatinib', 'Tazemetostat', 'Moderate', 'Coadministration with CYP450 3A4 inducers may decrease the plasma concentrations and anti-tumor activity of capmatinib. The proposed mechanism involves induction of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', 'DDInter', 1767317475, 'Caution is recommended whenever capmatinib is used with a CYP450 3A4 inducer. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inducer is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity.', 'Metabolism', 'Caution is recommended whenever capmatinib is used with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
