INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379360, 'Capmatinib', 'Zolmitriptan', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of zolmitriptan, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'It is recommended that not more than 5 mg of zolmitriptan be taken in a 24-hour period during concomitant use with CYP450 1A2 inhibitors. Patients should be advised to notify their doctor promptly if they experience increased sedation, dizziness, paraesthesia, chest pain or tightness, shortness of breath, and/or irregular heartbeat.', 'Metabolism', 'It is recommended that not more than 5 mg of zolmitriptan be taken in a 24-hour period during concomitant use with CYP450 1A2 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379361, 'Amikacin (liposome)', 'Capreomycin', 'Major', 'The combination of a polypeptide antibiotic and an aminoglycoside may increase the risk of neurotoxicity, respiratory paralysis, and renal dysfunction. The mechanism of this interaction is not known, but may involve additive or synergistic effects.', 'DDInter', 1767317475, 'This combination should be avoided concurrently or sequentially. If coadministration is medically necessary, patients should be closely monitored for development of toxicity, including renal function tests, respiratory function, and vestibular and audiometric tests.', 'Synergism', 'This combination should be avoided concurrently or sequentially.', NULL, NULL, 'Major', 'pharmacodynamic');
