INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92503, 'Dronedarone', 'Metipranolol (ophthalmic)', 'Moderate', 'Coadministration with dronedarone may increase the plasma concentrations of some beta-blockers. The mechanism is dronedarone inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of beta-blockers like metoprolol and propranolol. Pharmacodynamically, beta-blockers can potentiate the depressant effects of dronedarone on the sinus and AV nodes.', NULL, 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations.', NULL, 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations. The dosage of the beta-blocker should start low and increased only after ECG verification of good tolerability. Patients should be advised to promptly report potential symptoms of toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or arrhythmia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92607/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92504, 'Dronedarone', 'Metoclopramide', 'Moderate', 'Coadministration with dronedarone may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter.', NULL, 'Caution is advised when dronedarone is used concomitantly with drugs that are substrates of CYP450 2D6, CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when dronedarone is used concomitantly with drugs that are substrates of CYP450 2D6, CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dronedarone is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92608/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92505, 'Dronedarone', 'Metolazone', 'Major', 'Dronedarone may cause dose-related prolongation of the QT interval. Since hypokalemia and hypomagnesemia are known risk factors for arrhythmia associated with QT prolongation, coadministration of dronedarone with agents that can cause significant potassium and/or magnesium loss (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may increase the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Caution is advised if dronedarone must be used concomitantly with medications that can cause significant potassium and/or magnesium loss.', NULL, 'Caution is advised if dronedarone must be used concomitantly with medications that can cause significant potassium and/or magnesium loss. Serum electrolytes should be evaluated at baseline and at regular intervals throughout the duration of therapy. Any abnormalities should be corrected prior to initiating therapy, and levels should be maintained within the normal range during treatment. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Dronedarone should be discontinued if QTc Bazett interval increases to 500 ms or greater.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92609/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92506, 'Dronedarone', 'Metoprolol', 'Moderate', 'Coadministration with dronedarone may increase the plasma concentrations of some beta-blockers. The mechanism is dronedarone inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of beta-blockers like metoprolol and propranolol. Pharmacodynamically, beta-blockers can potentiate the depressant effects of dronedarone on the sinus and AV nodes.', NULL, 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations.', NULL, 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations. The dosage of the beta-blocker should start low and increased only after ECG verification of good tolerability. Patients should be advised to promptly report potential symptoms of toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or arrhythmia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92610/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92507, 'Dronedarone', 'Metronidazole', 'Minor', 'Limited data suggest that metronidazole may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with metronidazole.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92611/', '[1] Kounas SP, Letsas KP, Sideris A, Efraimidis M, Kardaras F "QT interval prolongation and torsades de pointes due to a coadministration of metronidazole and amiodarone." Pacing Clin Electrophysiol 28 (2005): 472-3[2] Giannini AJ, DeFrance DT "Metronidazole and alcohol: potential for combinative abuse." J Toxicol Clin Toxicol 20 (1983): 509-15[3] "Product Information. Benznidazole (benznidazole)." Everett Laboratories Inc, West Orange, NJ.[4] Williams CS, Woodcock KR "Do ethanol and metronidazole interact to produce a disulfiram-like reaction?." Ann Pharmacother 34 (2000): 255-7[5] Krulewitch CJ "An unexpected adverse drug effect." J Midwifery Womens Health 48 (2003): 67-8[6] Alexander I "Alcohol-antabuse syndrome in patients receiving metronidazole during gynaecological treatment." Br J Clin Pract 39 (1985): 292-3[7] "Product Information. Flagyl (metronidazole)." Searle, Skokie, IL.[8] Harries DP, Teale KF, Sunderland G "Metronidazole and alcohol: potential problems." Scott Med J 35 (1990): 179-80[9] Visapaa JP, Tillonen JS, Kaiho