INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92686, 'Dronedarone', 'Timolol', 'Moderate', 'Coadministration with dronedarone may increase the plasma concentrations of some beta-blockers. The mechanism is dronedarone inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of beta-blockers like metoprolol and propranolol. Pharmacodynamically, beta-blockers can potentiate the depressant effects of dronedarone on the sinus and AV nodes.', NULL, 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations.', NULL, 'Caution is advised if dronedarone is coadministered with a beta-blocker, including ophthalmic formulations. The dosage of the beta-blocker should start low and increased only after ECG verification of good tolerability. Patients should be advised to promptly report potential symptoms of toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or arrhythmia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92790/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.', 1767365205);
