INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92725, 'Dronedarone', 'Vismodegib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein may increase the plasma concentrations of vismodegib, which has been shown in vitro to be a substrate of the efflux transporter. The potential for increased pharmacologic effects of vismodegib, including adverse effects such as muscle spasm, alopecia, dysgeusia/ageusia, weight loss, fatigue, nausea, vomiting, diarrhea, decreased appetite, constipation and arthralgia, should be considered.', NULL, 'Caution is advised if vismodegib is used in combination with P-glycoprotein inhibitors.', NULL, 'Caution is advised if vismodegib is used in combination with P-glycoprotein inhibitors.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92829/', '[1] "Product Information. Erivedge (vismodegib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92726, 'Dronedarone', 'Voriconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of dronedarone, which is primarily metabolized by the isoenzyme. Because dronedarone is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'The use of dronedarone in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, cyclosporine, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated.', NULL, 'The use of dronedarone in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, cyclosporine, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated. Patients currently receiving these agents should stop taking them before starting treatment with dronedarone. Some authorities consider concomitant administration of dronedarone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92830/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[4] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92727, 'Dronedarone', 'Voxelotor', 'Moderate', 'Coadministration of dronedarone with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of both drugs. Dronedarone itself is also a substrate and moderate inhibitor of CYP450 3A4. Theoretically, competitive and/or noncompetitive metabolic inhibition may occur. Because dronedarone is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Caution is advised if dronedarone is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, 'Caution is advised if dronedarone is prescribed in combination with a drug that is an inhibitor of CYP450 3A4. Pharmacologic response to dronedarone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to seek medical attention if they experience symptoms that could indicate dronedarone toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or symptoms of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, and syncope. Likewise, clinical and/or laboratory monitoring may be appropriate for the coadministered drug following the addition or withdrawal of dronedarone, and the dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92831/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis 