INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92850, 'Droperidol', 'Levomilnacipran', 'Moderate', 'Coadministration of milnacipran or levomilnacipran with neuroleptic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', NULL, 'Caution is advised if milnacipran or levomilnacipran is prescribed in combination with neuroleptic agents.', NULL, 'Caution is advised if milnacipran or levomilnacipran is prescribed in combination with neuroleptic agents. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures. Patients receiving milnacipran or levomilnacipran with neuroleptic agents should be apprised of potentially additive CNS effects and avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/92954/', '[1] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92851, 'Droperidol', 'Levorphanol', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce bradycardia, a known risk factor for QT interval prolongation, such as benzodiazepines and opiates, particularly intravenous opiates, may increase the risk of QT interval prolongation. In addition, hypotensive effects and central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking droperidol with benzodiazepines or opiates, especially in elderly or debilitated patients.', NULL, 'Extreme caution and close monitoring are recommended if droperidol must be administered concomitantly with other bradycardic drugs.', NULL, 'Extreme caution and close monitoring are recommended if droperidol must be administered concomitantly with other bradycardic drugs. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with benzodiazepines or opiates, patients should be monitored for potentially excessive or prolonged CNS or respiratory depression as well as severe hypotension. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://dd