INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92940, 'Droperidol', 'Pergolide', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', NULL, 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', NULL, 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93044/', '[1] Weingarten JC, Thompson TL "The effect of thioridazine on prolactinoma growth in a schizophrenic man: case report." Gen Hosp Psychiatry 7 (1985): 364-6[2] "Product Information. Norprolac (quinagolide)." Ferring Inc, North York, IA.[3] "Product Information. Requip (ropinirole)." SmithKline Beecham, Philadelphia, PA.[4] "Product Information. Reglan (metoclopramide)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[7] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[8] "Product Information. Mirapex (pramipexole)." Boehringer Ingelheim, Ridgefield, CT.[9] "Product Information. Dostinex (cabergoline)." Pharmacia and Upjohn, Kalamazoo, MI.[10] Robbins RJ, Kern PA, Thompson TL "Interactions between thioridazine and bromocriptine in a patient with a prolactin-secreting pituitary adenoma." Am J Med 76 (1984): 921-3[11] Yahr MD, Duvoisin RC "Drug therapy of parkinsonism." N Engl J Med 287 (1972): 20-4[12] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[13] Mims RB, Scott CL, Modebe O, Bethune JE "Inhibition of L-dopa-induced growth hormone stimulation by pyridoxine and chlorpromazine." J Clin Endocrinol Metab 40 (1975): 256-9[14] "Product Information. Permax (pergolide)." Athena Neurosciences Inc, South San Francisco, CA.[15] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[16] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[17] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[18] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[19] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[20] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92941, 'Droperidol', 'Perphenazine', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins), drugs known to increase the QT interval (e.g., phenothiazines, tricyclic antidepressants, antiarrhythmic agents, etc.), certain other drugs (benzodiazepines, volatile anesthetics, intravenous opiates), or alcohol abuse may increase the risk of prolonged QT syndrome.', NULL, 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents.', NULL, 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93045/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing