INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92986, 'Droperidol', 'Rivastigmine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93090/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (92987, 'Droperidol', 'Rofecoxib', 'Moderate', 'Coadministration with drugs that are potent inhibitors of CYP450 3A4 and/or 1A2 may increase the plasma concentrations of droperidol, which is predicted to be metabolized by these isoenzymes. While clinical data are lacking, the possibility of prolonged pharmacological action and adverse effects such as central nervous system depression, extrapyramidal reactions, tardive dyskinesia, hypotension, and prolongation of the QT interval should be considered.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of droperidol should be considered during coadministration of potent inhibitors of CYP450 3A4 and/or 1A2.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of droperidol should be considered during coadministration of potent inhibitors of CYP450 3A4 and/or 1A2. Some manufacturers advise that to minimize the risk of ventricular arrhythmia, an ECG should be performed in all patients prior to initiating therapy with droperidol and periodically thereafter. Therapeutic response to droperidol should be monitored more closely following initiation, discontinuation, or dosing change of the concomitant inhibitor, and the droperidol dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93091/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] Lemma GL, Desta Z, Jones DR, Gorski JC, Hall SD "Droperidol is predominantly metabolized by human CYP3A enzymes." Clin Pharmacol Ther 73 (2003): 16[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Droperidol (droperidol)." Abbott Pharmaceutical, Abbott Park, IL.[5] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Inapsine (droperidol)." Janssen Pharmaceutica, Titusville, NJ.', 1767365205);
