INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93133, 'Irbesartan', 'Drospirenone', 'Moderate', 'The use of drospirenone may potentiate the risk of hyperkalemia in patients receiving chronic treatment with drugs that can increase serum potassium such as angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium preparations, and miscellaneous agents like cyclosporine, heparin, tacrolimus, and trimethoprim. Drospirenone has antimineralocorticoid activity and may induce hyperkalemia in susceptible patients.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium.', NULL, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93237/', '[1] Ponce SP, Jennings AE, Madias NE, Harrington JT "Drug-induced hyperkalemia." Medicine (Baltimore) 64 (1985): 357-70[2] "Product Information. Yasmin (drospirenone-ethinyl estradiol)" Berlex Laboratories, Richmond, CA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93134, 'Itraconazole', 'Drospirenone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of drospirenone which is primarily metabolized by the isoenzyme. High exposure to drospirenone may potentiate the risk hyperkalemia. Drospirenone has antimineralocorticoid activity and can induce hyperkalemia in susceptible patients.', NULL, 'Caution and close clinical monitoring are recommended, especially in patients requiring long-term use and those at increased risk for hyperkalemia (e.', NULL, 'Caution and close clinical monitoring are recommended, especially in patients requiring long-term use and those at increased risk for hyperkalemia (e.g., patients with renal impairment, those receiving potassium-sparing medications). Because of the risk for hyperkalemia, concomitant use with certain CYP450 3A4 inhibitors such as boceprevir or atazanavir boosted with cobicistat, is considered contraindicated by the manufacturers. Consider checking potassium levels during the first treatment cycle of concomitant drospirenone use and then periodically as indicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93238/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[3] "Product Information. Symtuza (cobicistat/darunavir/emtricitabine/tenof)." Janssen Pharmaceuticals, Titusville, NJ.[4] "Product Information. Yasmin (drospirenone-ethinyl estradiol)" Berlex Laboratories, Richmond, CA.[5] "Product Information. Slynd (drospirenone)." Exe