INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93152, 'Drospirenone', 'Lonafarnib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of drospirenone which is primarily metabolized by the isoenzyme. High exposure to drospirenone may potentiate the risk hyperkalemia. Drospirenone has antimineralocorticoid activity and can induce hyperkalemia in susceptible patients.', NULL, 'Caution and close clinical monitoring are recommended, especially in patients requiring long-term use and those at increased risk for hyperkalemia (e.', NULL, 'Caution and close clinical monitoring are recommended, especially in patients requiring long-term use and those at increased risk for hyperkalemia (e.g., patients with renal impairment, those receiving potassium-sparing medications). Because of the risk for hyperkalemia, concomitant use with certain CYP450 3A4 inhibitors such as boceprevir or atazanavir boosted with cobicistat, is considered contraindicated by the manufacturers. Consider checking potassium levels during the first treatment cycle of concomitant drospirenone use and then periodically as indicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93256/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[3] "Product Information. Symtuza (cobicistat/darunavir/emtricitabine/tenof)." Janssen Pharmaceuticals, Titusville, NJ.[4] "Product Information. Yasmin (drospirenone-ethinyl estradiol)" Berlex Laboratories, Richmond, CA.[5] "Product Information. Slynd (drospirenone)." Exeltis USA, Inc., Chatham, NJ.[6] "Product Information. Prezcobix (cobicistat-darunavir)." Janssen Pharmaceuticals, Titusville, NJ.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[9] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93153, 'Drospirenone', 'Lorlatinib', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', NULL, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93257/', '[1] Back DJ, Breckenridge AM, Crawford FE, MacIver M, Orne ML, Rowe PH "Interindividual variation and drug interactions with hormonal steroid contraceptives." Drugs 21 (1981): 46-61[2] van Dijke CP, Weber JC "Interaction between oral contraceptives and griseofulvin." Br Med J (Clin Res Ed) 288 (1984): 1125-6[3] Dickinson BD, Altman RD, Nielsen NH, Sterling ML "Drug interactions between oral contraceptives and antibiotics." Obstet Gynecol 98(5 Pt 1) (2001): 853-