INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379557, 'Interferon alfa-2a, Recombinant', 'Captopril', 'Moderate', 'The concomitant use of recombinant interferon-alpha-2a and some angiotensin converting enzyme (ACE) inhibitors has been associated with a higher incidence of granulocytopenia than either drug used alone in patients with cryoglobulinemia. The mechanism is unknown but may be related to an autoimmune response. It is also unknown whether patients with other conditions may be affected. Data exist for enalapril and captopril.', 'DDInter', 1767317475, 'Until more information is available, close hematologic monitoring is recommended.', 'Synergism', 'Until more information is available, close hematologic monitoring is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379558, 'Interferon beta-1a', 'Captopril', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379559, 'Interferon beta-1b', 'Captopril', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379560, 'Irbesartan', 'Captopril', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', 'DDInter', 1767317475, 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy. Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379561, 'Iron Dextran', 'Captopril', 'Major', 'Limited data suggest that ACE inhibitors may increase the risk of systemic adverse effects associated with the use of iron dextran. The exact mechanism of interaction is unclear. Certain systemic reactions stemming from parenteral iron therapy are thought to be mediated by inflammatory substances such as bradykinin in response to iron-catalyzed generation of toxic free radicals. Since ACE inhibitors decrease the breakdown of kinins, it is conceivable that they may potentiate these reactions. Anaphylactic-type reactions, including fatalities, have followed the parenteral administration of iron dextran. Such reactions occur most often within the first several minutes of administration and are generally characterized by sudden onset of respiratory difficulty and/or cardiovascular collapse. The extent of risk for anaphylactic-type reactions following exposure to any specific iron dextran product is unknown, but may vary among the products due to differences in chemical characteristics.', 'DDInter', 1767317475, 'Patients should be closely monitored during and after parenteral administration of iron dextran, regardless of whether they are being treated with an ACE inhibitor. This is particularly important in patients with a history of drug allergy or multiple drug allergies or an immune/inflammatory condition such as systemic lupus erythematosus or rheumatoid arthritis. Resuscitation techniques and personnel trained in the detection and treatment of anaphylactic-type reactions should be readily available. Prior to the first therapeutic dose, a test dose corresponding to 25 mg iron should be injected gradually. Although anaphylactic reactions are usually evident within a few minutes, observe patients for at least one hour before administering the remainder of the therapeutic dose. Administration must be stopped immediately if signs of an anaphylactoid reaction are observed. Patients should also be closely monitored during each subsequent administration of iron dextran. Fatal reactions have occurred following the test dose and also in situations where the test dose was tolerated. According to some studies, non-dextran parenteral iron formulations may be associated with a lower risk of adverse effects, especially death and life-threatening reactions such as anaphylaxis, cardiac arrest, and respiratory depression.', 'Synergism', 'Patients should be closely monitored during and after parenteral administration of iron dextran, regardless of whether they are being treated with an ACE inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379562, 'Iron sucrose', 'Captopril', 'Moderate', 'Limited data suggest that ACE inhibitors may increase the risk and/or severity of systemic adverse effects associated with parenteral administration of iron. The exact mechanism of interaction is unclear. Certain systemic reactions stemming from intravenous iron therapy are thought to be mediated by inflammatory substances such as bradykinin in response to iron-catalyzed generation of toxic free radicals. Since ACE inhibitors decrease the breakdown of kinins, it is conceivable that they may potentiate these reactions.', 'DDInter', 1767317475, 'No specific intervention is necessary to avert a potential interaction. However, patients should be closely monitored during and after parenteral iron treatment.', 'Others', 'No specific intervention is necessary to avert a potential interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379563, 'Isocarboxazid', 'Captopril', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 1767317475, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379564, 'Isosorbide dinitrate', 'Captopril', 'Moderate', 'Angiotensin converting enzyme (ACE) inhibitors may enhance the vasodilatory and hypotensive effects of nitroglycerin. Data have also shown that captopril can prevent nitrate tolerance. ACE inhibitors can decrease systemic vascular resistance and cardiac work, further enhancing the effectiveness of nitroglycerin.', 'DDInter', 1767317475, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose. Blood pressure monitoring is advisable.', 'Synergism', 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379565, 'Isosorbide mononitrate', 'Captopril', 'Moderate', 'Angiotensin converting enzyme (ACE) inhibitors may enhance the vasodilatory and hypotensive effects of nitroglycerin. Data have also shown that captopril can prevent nitrate tolerance. ACE inhibitors can decrease systemic vascular resistance and cardiac work, further enhancing the effectiveness of nitroglycerin.', 'DDInter', 1767317475, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose. Blood pressure monitoring is advisable.', 'Synergism', 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379566, 'Isradipine', 'Captopril', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379567, 'Captopril', 'Ketoprofen', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and ACE inhibitors may also cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible. Chronic use of NSAIDs alone may be associated with renal toxicities, including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', 'DDInter', 1767317475, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379568, 'Captopril', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: Theoretically, lanthanum carbonate may chelate with certain drugs in the gastrointestinal tract, resulting in reduced oral bioavailability of those drugs during coadministration.', 'DDInter', 1767317475, 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.g., ACE inhibitors, beta blockers, bisphosphonates, coumarin derivatives, digitalis glycosides, fluoroquinolones, iron, phenytoin, rifampin, tetracyclines, thyroid preparations, valproic acid) should not be taken within 2 hours of administration of lanthanum carbonate according to the product labeling.', 'Absorption', 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379569, 'Captopril', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317475, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379570, 'Captopril', 'Levamlodipine', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379571, 'Captopril', 'Levodopa', 'Moderate', 'The hypotensive effects of levodopa and antihypertensive agents may be additive. Postural hypotension may occur.', 'DDInter', 1767317475, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Dose adjustments of the antihypertensive agent may be required. Patients should be advised to notify their physician if they experience dizziness or syncope.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379572, 'Captopril', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', 'DDInter', 1767317475, 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379573, 'Captopril', 'Linagliptin', 'Moderate', 'Limited clinical data suggest that dipeptidyl peptidase IV (DPP IV) inhibitors, e.g., gliptins, have been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema, such as angiotensin converting enzyme (ACE) inhibitors may theoretically increase the risk.', 'DDInter', 1767317475, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when inhibitors are prescribed with ACE inhibitors. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing. Close monitoring for the development of hypoglycemia is recommended if ACE inhibitors are coadministered with inhibitors, particularly in patients with advanced age and/or renal impairment and during the first month of treatment. Dosage adjustment may be required if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when inhibitors are prescribed with ACE inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379574, 'Captopril', 'Linezolid', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 1767317475, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379575, 'Captopril', 'Lithium carbonate', 'Major', 'Coadministration with angiotensin converting enzyme (ACE) inhibitors may increase serum lithium concentrations and the risk for lithium toxicity. Several mechanisms may be involved, one of which is reduced renal lithium clearance due to natriuresis secondary to the inhibition of aldosterone and angiotensin II by ACE inhibitors. The combination may also cause renal dysfunction secondary to volume depletion during chronic therapy, which can further impair lithium clearance. The interaction was suspected in cases of lithium toxicity that occurred up to several weeks after the initiation of ACE inhibitor therapy.', 'DDInter', 1767317475, 'Given the narrow therapeutic index of lithium, caution is advised during coadministration with ACE inhibitors, particularly in the elderly or patients with other risk factors (e.g., sodium restriction, renal impairment, congestive heart failure, dehydration, concomitant use of diuretics or NSAIDs). Pharmacologic response and serum lithium levels should be monitored more closely whenever an ACE inhibitor is added to or withdrawn from therapy, and the lithium dosage adjusted as necessary. Empiric reductions of both drugs may be appropriate during initial therapy. Renal function should also be monitored regularly. Patients should be advised to seek medical attention if they experience potential signs and symptoms of lithium toxicity such as drowsiness, dizziness, confusion, muscle weakness, vomiting, diarrhea, polydipsia, polyuria, tinnitus, tremor, ataxia, and blurred vision.', 'Excretion', 'Given the narrow therapeutic index of lithium, caution is advised during coadministration with ACE inhibitors, particularly in the elderly or patients with other risk factors (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379576, 'Captopril', 'Lofexidine', 'Moderate', 'The hypotensive effects of lofexidine and antihypertensive agents may be additive. Postural hypotension may occur.', 'DDInter', 1767317475, 'Hemodynamic responses should be monitored during coadministration of lofexidine with antihypertensive agents, especially during the first few weeks of therapy.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration of lofexidine with antihypertensive agents, especially during the first few weeks of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379577, 'Captopril', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', 'DDInter', 1767317475, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379578, 'Captopril', 'Lorazepam', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379579, 'Captopril', 'Losartan', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', 'DDInter', 1767317475, 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy. Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', NULL, NULL, 'Major', 'pharmacodynamic');
