INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379618, 'Captopril', 'Nitroglycerin', 'Moderate', 'Angiotensin converting enzyme (ACE) inhibitors may enhance the vasodilatory and hypotensive effects of nitroglycerin. Data have also shown that captopril can prevent nitrate tolerance. ACE inhibitors can decrease systemic vascular resistance and cardiac work, further enhancing the effectiveness of nitroglycerin.', 'DDInter', 1767317475, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose. Blood pressure monitoring is advisable.', 'Synergism', 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379619, 'Captopril', 'Nitroprusside', 'Minor', 'The dosage of nitroprusside required to achieve a given hypotensive response is significantly reduced when captopril is coadministered. The mechanism is unknown. Since the risk of side effects from nitroprusside may be significantly decreased with lower doses, this interaction may be advantageous.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
