INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379637, 'Captopril', 'Pentoxifylline', 'Moderate', 'Pentoxifylline may potentiate the action of hypotensive agents. Small decreases in blood pressure have been observed in some patients treated with pentoxifylline plus nifedipine or captopril. The mechanism of interaction has not been described.', 'DDInter', 1767317475, 'Caution is advised if pentoxifylline is used in combination with hypotensive agents. Periodic systemic blood pressure monitoring is recommended.', 'Synergism', 'Caution is advised if pentoxifylline is used in combination with hypotensive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379638, 'Captopril', 'Pergolide', 'Moderate', 'One patient, whose hypertension had been treated with lisinopril for three years, experienced a severe hypotensive episode after receiving a 0.05 mg dose of pergolide. The mechanism is unknown and causality has not been clearly established.', 'DDInter', 1767317475, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors. Dose adjustments may be required.', 'Others', 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
