INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379827, 'Aminoglutethimide', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of carbamazepine, which is primarily metabolized by the isoenzyme. The interaction has been reported with known CYP450 3A4 inducers such as phenobarbital, phenytoin, and primidone.', 'DDInter', 1767317475, 'Pharmacologic effects and serum concentrations of carbamazepine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the carbamazepine dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic effects and serum concentrations of carbamazepine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the carbamazepine dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379828, 'Aminophylline', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease theophylline serum levels. Also, theophyllines may decrease serum levels and effects of carbamazepine. The mechanisms may be related to induction of hepatic CYP450 metabolism.', 'DDInter', 1767317475, 'Close observation for clinical and laboratory evidence of altered effects is recommended for both agents. Patients should be advised to notify their physician if they experience loss of therapeutic effects.', 'Metabolism', 'Close observation for clinical and laboratory evidence of altered effects is recommended for both agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379829, 'Amoxapine', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease serum concentrations of tricyclic antidepressants. The mechanism may be related to carbamazepine induction of CYP450 enzymes responsible for tricyclic antidepressant metabolism. Serum concentrations of carbamazepine may be increased by an unknown mechanism. Additionally, tricyclic antidepressants may counteract the anticonvulsant effects of carbamazepine by lowering the seizure threshold.', 'DDInter', 1767317475, 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', 'Metabolism', 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379830, 'Amprenavir', 'Carbamazepine', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379831, 'Anisindione', 'Carbamazepine', 'Moderate', 'Carbamazepine may induce the metabolism of warfarin and reduce its anticoagulant effect. The mechanism is induction of hepatic isoenzyme metabolism by carbamazepine. Carbamazepine may interact with other oral anticoagulants in a similar fashion.', 'DDInter', 1767317475, 'INR should be monitored whenever carbamazepine is started or discontinued in a patient receiving an oral anticoagulant. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Metabolism', 'INR should be monitored whenever carbamazepine is started or discontinued in a patient receiving an oral anticoagulant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379832, 'Carbamazepine', 'Artesunate', 'Moderate', 'Coadministration with potent inducers of uridine diphosphate glucuronosyltransferase (UGT) may decrease the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate and substrate of UGT 1A9 or 2B7. Reduced efficacy of artesunate may occur.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of artesunate should be considered during coadministration with potent UGT inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of artesunate should be considered during coadministration with potent UGT inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379833, 'Carbamazepine', 'Atracurium', 'Moderate', 'Carbamazepine may reduce the effects or duration of nondepolarizing neuromuscular blocking agents. The mechanism is unknown.', 'DDInter', 1767317475, 'Close observation for decreased neuromuscular blocking effect is recommended if a patient on carbamazepine must receive a nondepolarizing neuromuscular blocking agent.', 'Antagonism', 'Close observation for decreased neuromuscular blocking effect is recommended if a patient on carbamazepine must receive a nondepolarizing neuromuscular blocking agent.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379834, 'Carbamazepine', 'Azatadine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379835, 'Carbamazepine', 'Bedaquiline', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2. Because M2 is 4- to 6-fold less active than the parent drug in terms of antimycobacterial potency, the interaction may diminish the therapeutic effect of bedaquiline.', 'DDInter', 1767317475, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379836, 'Carbamazepine', 'Bendroflumethiazide', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 1767317475, 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379837, 'Carbamazepine', 'Benzthiazide', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 1767317475, 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379838, 'Carbamazepine', 'Bepridil', 'Major', 'Coadministration with carbamazepine may significantly decrease the plasma concentrations and pharmacologic effects of calcium channel blockers (CCBs), especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nimodipine, nisoldipine). The proposed mechanism is carbamazepine induction of CYP450-mediated metabolism.', 'DDInter', 1767317475, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers such as carbamazepine should generally be avoided. If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation, discontinuation or change of dosage of carbamazepine, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers such as carbamazepine should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379839, 'Carbamazepine', 'Berotralstat', 'Major', 'Coadministration with inducers of P-glycoprotein (P-gp) may significantly decrease the plasma concentrations and pharmacologic effects of berotralstat, a substrate of this efflux transporter.', 'DDInter', 1767317475, 'Due to the potential for diminished pharmacologic effects of berotralstat, its concomitant use with P-gp inducers, including but not limited to rifampin and St. John''s wort, should be avoided. Alternative therapeutic agents with less P-gp induction potential should be considered whenever possible.', 'Excretion', 'Due to the potential for diminished pharmacologic effects of berotralstat, its concomitant use with P-gp inducers, including but not limited to rifampin and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379840, 'Carbamazepine', 'Boceprevir', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of boceprevir, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379841, 'Carbamazepine', 'Butabarbital', 'Minor', 'Coadministration with a barbiturate may decrease the plasma concentrations of carbamazepine and increase concentrations of its active 10,11-epoxide metabolite. The mechanism is barbiturate induction of CYP450 3A4, the isoenzyme responsible for the metabolism of carbamazepine to carbamazepine-10,11-epoxide (CBZ-E). Until more data are available, it may be reasonable to monitor serum carbamazepine levels more closely whenever a barbiturate (if used for more than a few days) is added to or withdrawn from therapy.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379842, 'Carbamazepine', 'Butalbital', 'Minor', 'Coadministration with a barbiturate may decrease the plasma concentrations of carbamazepine and increase concentrations of its active 10,11-epoxide metabolite. The mechanism is barbiturate induction of CYP450 3A4, the isoenzyme responsible for the metabolism of carbamazepine to carbamazepine-10,11-epoxide (CBZ-E). Until more data are available, it may be reasonable to monitor serum carbamazepine levels more closely whenever a barbiturate (if used for more than a few days) is added to or withdrawn from therapy.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
