INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379856, 'Chlordiazepoxide', 'Carbamazepine', 'Moderate', 'Some antiepileptic agents such as carbamazepine and phenytoin significantly reduce the effect of oral midazolam. The mechanism may be due to enhanced gut and liver metabolism of midazolam (via CYP450 3A4 enzyme induction by carbamazepine). Parenteral midazolam is not likely to be affected. Other oral benzodiazepines metabolized by the 3A4 isoenzyme may interact similarly.', 'DDInter', 1767317475, 'Patients receiving this combination should be monitored for clinical response. Alternative oral sedative hypnotics may be preferable in patients receiving carbamazepine.', 'Metabolism', 'Patients receiving this combination should be monitored for clinical response.', NULL, NULL, 'Moderate', 'pharmacodynamic');
