INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379870, 'Clindamycin', 'Carbamazepine', 'Moderate', 'Coadministration with strong CYP450 3A4 inducers may decrease plasma concentrations and antimicrobial effects of clindamycin, which is metabolized predominantly by the isoenzyme.', 'DDInter', 1767317475, 'Monitor closely for diminished therapeutic response to clindamycin during coadministration with strong CYP450 3A4 inducers. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy with clindamycin. An alternative agent with no or minimal CYP450 3A4-inducing activity may also be considered.', 'Metabolism', 'Monitor closely for diminished therapeutic response to clindamycin during coadministration with strong CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379871, 'Clobazam', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379872, 'Clofarabine', 'Carbamazepine', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', 'DDInter', 1767317475, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379873, 'Clomipramine', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease serum concentrations of tricyclic antidepressants. The mechanism may be related to carbamazepine induction of CYP450 enzymes responsible for tricyclic antidepressant metabolism. Serum concentrations of carbamazepine may be increased by an unknown mechanism. Additionally, tricyclic antidepressants may counteract the anticonvulsant effects of carbamazepine by lowering the seizure threshold.', 'DDInter', 1767317475, 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', 'Metabolism', 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379874, 'Clonazepam', 'Carbamazepine', 'Moderate', 'Carbamazepine may reduce serum clonazepam levels. The mechanism may be related to induction of metabolism. The risk of CNS-depressant side effects, such as sedation and apathy, may be increased with this combination.', 'DDInter', 1767317475, 'If these drugs must be used together, observation for clinical and laboratory evidence of altered clonazepam effect is recommended.', 'Metabolism', 'If these drugs must be used together, observation for clinical and laboratory evidence of altered clonazepam effect is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379875, 'Clonidine', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379876, 'Clorazepic acid', 'Carbamazepine', 'Moderate', 'Some antiepileptic agents such as carbamazepine and phenytoin significantly reduce the effect of oral midazolam. The mechanism may be due to enhanced gut and liver metabolism of midazolam (via CYP450 3A4 enzyme induction by carbamazepine). Parenteral midazolam is not likely to be affected. Other oral benzodiazepines metabolized by the 3A4 isoenzyme may interact similarly.', 'DDInter', 1767317475, 'Patients receiving this combination should be monitored for clinical response. Alternative oral sedative hypnotics may be preferable in patients receiving carbamazepine.', 'Metabolism', 'Patients receiving this combination should be monitored for clinical response.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379877, 'Clozapine', 'Carbamazepine', 'Major', 'The use of clozapine with other potentially myelotoxic agents such as carbamazepine may increase the risk and/or severity of hematologic toxicity. Clozapine alone is associated with a significant risk of agranulocytosis, defined as an absolute neutrophil count (ANC) of less than 500/mm3. Case reports also suggest that carbamazepine may decrease the pharmacologic effects of clozapine. The mechanism may involve induction of CYP450 hepatic metabolism by carbamazepine. Increased serum clozapine levels have been reported after discontinuation of carbamazepine therapy.', 'DDInter', 1767317475, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', 'Metabolism, Synergism', 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379878, 'Cobimetinib', 'Carbamazepine', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of cobimetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of cobimetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379879, 'Codeine', 'Carbamazepine', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', 'DDInter', 1767317475, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379880, 'Colesevelam', 'Carbamazepine', 'Moderate', 'INTERVAL: Colesevelam may decrease the gastrointestinal absorption of coadministered drugs. Approximately 20% to 40% reductions in systemic exposure (AUC) and 10% to 45% reductions in peak plasma concentration (Cmax) have been reported for cyclosporine, ethinyl estradiol, glimepiride, glyburide, levothyroxine, olmesartan, and sustained-release verapamil during coadministration with colesevelam.', 'DDInter', 1767317475, 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose. Clinical and laboratory monitoring of drug levels and effects should be considered.', 'Absorption', 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379881, 'Conivaptan', 'Carbamazepine', 'Moderate', 'Coadministration with conivaptan may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by conivaptan.', 'DDInter', 1767317475, 'Caution is advised if conivaptan must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever conivaptan is added to or withdrawn from therapy, or the combination avoided altogether.', 'Metabolism', 'Caution is advised if conivaptan must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379882, 'Copanlisib', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of copanlisib, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317475, 'Concomitant use of copanlisib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of copanlisib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379883, 'Corticotropin', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', 'DDInter', 1767317475, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever carbamazepine is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379884, 'Carbamazepine', 'Tetracosactide', 'Moderate', 'Carbamazepine may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', 'DDInter', 1767317475, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever carbamazepine is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379885, 'Crizotinib', 'Carbamazepine', 'Major', 'Coadministration with strong CYP450 3A4 inducers may decrease plasma concentrations and pharmacologic effects of crizotinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of crizotinib with strong CYP450 3A4 inducers such as rifampin, carbamazepine, phenobarbital, and phenytoin should generally be avoided. Some authorities also advise against concomitant use with moderate CYP450 3A4 inducers.', 'Metabolism', 'Concomitant use of crizotinib with strong CYP450 3A4 inducers such as rifampin, carbamazepine, phenobarbital, and phenytoin should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
