INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379887, 'Cyclobenzaprine', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379888, 'Cyclophosphamide', 'Carbamazepine', 'Moderate', 'Coadministration with carbamazepine may result in decreased plasma concentrations of cyclophosphamide and thiotepa, while concentrations of their main active metabolites increase. The proposed mechanism is carbamazepine induction of CYP450 2B6 and 3A4, the isoenzymes responsible for the bioactivation of cyclophosphamide and thiotepa to 4-hydroxycyclophosphamide and tepa, respectively.', 'DDInter', 1767317475, 'Given the potential for interaction and the high degree of interpatient variability with respect to hepatic enzyme activities, the use of cyclophosphamide or thiotepa in combination with carbamazepine should be avoided if possible. Anticonvulsants with no significant effects on CYP450 hepatic enzymes such as valproic acid, lamotrigine, or gabapentin may be appropriate alternatives. If carbamazepine is required, consideration should be given to dosage reduction of the chemotherapeutic agents. In addition, plasma levels of the active metabolites should be monitored to guide further dosing.', 'Metabolism', 'Given the potential for interaction and the high degree of interpatient variability with respect to hepatic enzyme activities, the use of cyclophosphamide or thiotepa in combination with carbamazepine should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379889, 'Cyclosporine', 'Carbamazepine', 'Major', 'Carbamazepine may decrease cyclosporine serum concentrations to subtherapeutic levels due to induction of hepatic metabolism of cyclosporine.', 'DDInter', 1767317475, 'This combination should be avoided in patients where maintenance of therapeutic cyclosporine levels is critical. Cyclosporine serum concentrations should be monitored when carbamazepine is added to or discontinued from the patient''s regimen, or when the carbamazepine dosage is changed. Patients should be carefully monitored for lack of therapeutic effect. The dosage of cyclosporine may need to be increased.', 'Metabolism', 'This combination should be avoided in patients where maintenance of therapeutic cyclosporine levels is critical.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379890, 'Cyproheptadine', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379891, 'Dabrafenib', 'Carbamazepine', 'Moderate', 'Coadministration with potent inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of dabrafenib, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317475, 'Concomitant use of dabrafenib with potent CYP450 2C8 or 3A4 inducers should generally be avoided. Otherwise, patients should be closely monitored for potential loss of efficacy of dabrafenib.', 'Metabolism', 'Concomitant use of dabrafenib with potent CYP450 2C8 or 3A4 inducers should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379892, 'Daclatasvir', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentration of daclatasvir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of daclatasvir with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of daclatasvir with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379893, 'Danazol', 'Carbamazepine', 'Moderate', 'Danazol may increase carbamazepine levels by inhibiting the CYP450 3A4 hepatic metabolism of carbamazepine. Carbamazepine toxicity is possible.', 'DDInter', 1767317475, 'If these drugs must be used together, close observation for clinical and laboratory evidence of carbamazepine toxicity is recommended. Patients should be advised to report possible signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia). Dose adjustments may be required.', 'Metabolism', 'If these drugs must be used together, close observation for clinical and laboratory evidence of carbamazepine toxicity is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379894, 'Dantrolene', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379895, 'Dapsone (topical)', 'Carbamazepine', 'Minor', 'Theoretically, coadministration of dapsone with inducers of CYP450 3A4 such as rifamycins, anticonvulsants, barbiturates, and St. John''s wort may increase the formation of dapsone hydroxylamine, a metabolite associated with hemolysis. Dose-related hemolysis is the most common adverse event associated with oral dapsone in both patients with or without glucose-6-phosphate dehydrogenase (G6PD) deficiency.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379896, 'Darifenacin', 'Carbamazepine', 'Moderate', 'Theoretically, coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of darifenacin, which has been shown to be a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379897, 'Darolutamide', 'Carbamazepine', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Reduced efficacy of darolutamide may occur.', 'DDInter', 1767317475, 'Concomitant use of darolutamide with dual P-gp and potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of darolutamide with dual P-gp and potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379898, 'Darunavir', 'Carbamazepine', 'Major', 'Coadministration with carbamazepine, phenobarbital, phenytoin, or primidone may significantly reduce the plasma concentrations of darunavir. The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of darunavir.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, darunavir should not be used in combination with carbamazepine, phenobarbital, phenytoin, or primidone.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, darunavir should not be used in combination with carbamazepine, phenobarbital, phenytoin, or primidone.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379899, 'Dasatinib', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of dasatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of dasatinib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with dasatinib. If coadministration is required, a dosage increase for dasatinib may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., myelosuppression, bleeding complications, fluid retention, QT prolongation) is recommended if the dosage of dasatinib is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of dasatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379900, 'Deferasirox', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) may decrease the plasma concentrations of deferasirox, which is primarily metabolized by UGT1A1- and UGT1A3-mediated glucuronidation.', 'DDInter', 1767317475, 'The concomitant use of deferasirox with potent UGT inducers such as rifampin, phenytoin, phenobarbital, carbamazepine, and ritonavir should generally be avoided. Otherwise, consideration may be given to increasing the initial dosage of deferasirox by 50% during coadministration with potent UGT inducers, with subsequent adjustments made according to serum ferritin levels and clinical response.', 'Metabolism', 'The concomitant use of deferasirox with potent UGT inducers such as rifampin, phenytoin, phenobarbital, carbamazepine, and ritonavir should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379901, 'Deferiprone', 'Carbamazepine', 'Major', 'Coadministration of deferiprone and other drugs that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity. Deferiprone alone can cause agranulocytosis, which may be preceded by neutropenia.', 'DDInter', 1767317475, 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided. Otherwise, close monitoring of absolute neutrophil count (ANC) is recommended if coadministration is required.', 'Synergism', 'Concomitant use of deferiprone with other drugs known to be associated with neutropenia or agranulocytosis should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379902, 'Deflazacort', 'Carbamazepine', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.', 'DDInter', 1767317475, 'Concomitant use of deflazacort with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of deflazacort with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379903, 'Delavirdine', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of delavirdine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of delavirdine with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of delavirdine with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379904, 'Desipramine', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease serum concentrations of tricyclic antidepressants. The mechanism may be related to carbamazepine induction of CYP450 enzymes responsible for tricyclic antidepressant metabolism. Serum concentrations of carbamazepine may be increased by an unknown mechanism. Additionally, tricyclic antidepressants may counteract the anticonvulsant effects of carbamazepine by lowering the seizure threshold.', 'DDInter', 1767317475, 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', 'Metabolism', 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379905, 'Desmopressin', 'Carbamazepine', 'Moderate', 'The antidiuretic response to vasopressin or desmopressin may be potentiated by certain drugs, including nonsteroidal anti-inflammatory agents, selective serotonin reuptake inhibitors, tricyclic/tetracyclic antidepressants, carbamazepine, chlorpropamide, chlorpromazine, clofibrate, eslicarbazepine, fludrocortisone, haloperidol, lamotrigine, oxcarbazepine, urea, and some antineoplastic agents (e.g., vinca alkaloids, cisplatin, cyclophosphamide). These drugs can occasionally cause fluid retention, in some cases secondary to the syndrome of inappropriate antidiuretic hormone secretion (SIADH). Coadministration with vasopressin or desmopressin may, therefore, increase the risk of water intoxication and/or hyponatremia. Seizure and coma in association with severe hyponatremia have been reported during concomitant use of desmopressin and imipramine or ibuprofen.', 'DDInter', 1767317475, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH. Some authorities recommend adjusting vasopressin dosage as needed. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria, and weight gain. Severe hyponatremia can be life-threatening if it is not promptly diagnosed and treated; therefore, early treatment is important to help prevent progression to seizures, coma, respiratory arrest, and death.', 'Synergism', 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379906, 'Desvenlafaxine', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
