INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379967, 'Eszopiclone', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of zopiclone, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to zopiclone and eszopiclone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hypnotic dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to zopiclone and eszopiclone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hypnotic dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379968, 'Etanercept', 'Carbamazepine', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317475, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379969, 'Etacrynic acid', 'Carbamazepine', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 1767317475, 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379970, 'Ethchlorvynol', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379971, 'Ethosuximide', 'Carbamazepine', 'Minor', 'Carbamazepine may decrease serum levels of ethosuximide. The mechanism is related to carbamazepine induction of ethosuximide metabolism.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379972, 'Ethotoin', 'Carbamazepine', 'Moderate', 'Hydantoins may decrease carbamazepine levels, and carbamazepine may have variable effects on hydantoin levels. The mechanism may be related to induction of CYP450 hepatic metabolism of carbamazepine and alteration of hydantoin metabolism.', 'DDInter', 1767317475, 'Close observation for clinical and laboratory evidence of altered effects is recommended, particularly when one drug is started or discontinued. Patients should be advised to notify their physician if they experience loss of seizure control or symptoms of hydantoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia).', 'Metabolism', 'Close observation for clinical and laboratory evidence of altered effects is recommended, particularly when one drug is started or discontinued.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379973, 'Etonogestrel', 'Carbamazepine', 'Major', 'RECOMMENDED: Coadministration with certain anticonvulsants such as carbamazepine, eslicarbazepine, oxcarbazepine, phenobarbital, phenytoin, and primidone may reduce the efficacy of contraceptive hormones. There have been numerous case reports of menstrual abnormalities (e.g., breakthrough bleeding, amenorrhea, irregular menses) and unintended pregnancy occurring in women who received oral contraceptives with anticonvulsants. The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and hormone-binding globulin capacity by some anticonvulsants.', 'DDInter', 1767317475, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379974, 'Etoposide', 'Carbamazepine', 'Moderate', 'Some anticonvulsants such as carbamazepine, phenobarbital, phenytoin, and primidone may reduce the systemic exposure of etoposide. The proposed mechanism is accelerated clearance of etoposide due to induction of hepatic CYP450 enzymes by these anticonvulsants.', 'DDInter', 1767317475, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to etoposide, and the dosage adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to etoposide, and the dosage adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379975, 'Etravirine', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of etravirine, which is a substrate of CYP450 2C19, 2C9, and 3A4.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, etravirine should not be used with potent CYP450 inducers.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, etravirine should not be used with potent CYP450 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379976, 'Everolimus', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 1767317475, 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered. Some manufacturers recommend that, if concomitant use is unavoidable, the daily everolimus dose should be doubled to achieve the recommended therapeutic range for the condition being treated. Please refer to the manufacturer''s labeling for specific dosing information. If the potent CYP450 3A4 and/or P-gp inducer is discontinued, the everolimus dosage should be returned to the dosage used before the potent inducer was commenced after a washout period of approximately 3 to 5 days. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase, and 2 weeks after discontinuation of the potent inducers.', 'Metabolism', 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379977, 'Exemestane', 'Carbamazepine', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required. The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown. Caution is advised if they are used with exemestane.', 'Metabolism', 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379978, 'Ezogabine', 'Carbamazepine', 'Moderate', 'Coadministration with carbamazepine or phenytoin may decrease the plasma concentrations of ezogabine. The mechanism may involve induction of ezogabine metabolism via uridine 5''-diphosphate-glucuronyltransferases (UGTs).', 'DDInter', 1767317475, 'An increase in dosage of ezogabine should be considered when adding phenytoin or carbamazepine. Likewise, the ezogabine dosage may need to be reduced when these agents are discontinued.', 'Metabolism', 'An increase in dosage of ezogabine should be considered when adding phenytoin or carbamazepine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379979, 'Febuxostat', 'Carbamazepine', 'Minor', 'Potent inducers of UGT enzymes (e.g., rifampin, carbamazepine, phenobarbital, phenytoin, ritonavir) may increase the metabolism of febuxostat and decrease its therapeutic efficacy. Febuxostat undergoes extensive metabolism by both conjugation via UGT enzymes including UGT1A1, UGT1A3, UGT1A9, and UGT2B7, and oxidation via cytochrome P450 enzymes. The relative contribution of each isoenzyme and the clinical significance is unknown. Conversely, increased plasma levels of febuxostat may occur after a potent UGT inducer is discontinued.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379980, 'Fedratinib', 'Carbamazepine', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of fedratinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', 'Metabolism', 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379981, 'Felbamate', 'Carbamazepine', 'Moderate', 'Felbamate may increase the plasma clearance of carbamazepine but decrease the plasma clearance of carbamazepine epoxide (the metabolite that is one of the principal causes of carbamazepine side effects). Increased plasma carbamazepine epoxide levels may result. Additionally, carbamazepine may cause a 50% increase in the plasma clearance of felbamate.', 'DDInter', 1767317475, 'When combination therapy is started, felbamate should be initiated at 1200 mg per day in 3 to 4 divided doses while reducing the carbamazepine dose by 20% to 30%. In general, the felbamate dose may be titrated more quickly than when felbamate is given as monotherapy. Close observation for clinical and laboratory evidence of altered effects is indicated. Patients should be advised to report worsening of seizure control or signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) to their physician.', 'Metabolism, Excretion', 'When combination therapy is started, felbamate should be initiated at 1200 mg per day in 3 to 4 divided doses while reducing the carbamazepine dose by 20% to 30%.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379982, 'Felodipine', 'Carbamazepine', 'Moderate', '. The mechanism may be CYP450 enzyme induction by the anticonvulsants. Loss of antihypertensive efficacy may occur.', 'DDInter', 1767317475, 'Blood pressure should be monitored during concurrent administration. Dosage adjustments may be necessary. Alternative antihypertensives (e.g., amlodipine) may be considered.', 'Metabolism', 'Blood pressure should be monitored during concurrent administration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379983, 'Fesoterodine', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'No dosing adjustments are recommended when fesoterodine is used with CYP450 3A4 inducers. However, the possibility of diminished therapeutic effects should be considered.', 'Metabolism', 'No dosing adjustments are recommended when fesoterodine is used with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379984, 'Flavoxate', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379985, 'Flibanserin', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.', 'DDInter', 1767317475, 'Concomitant use of flibanserin with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of flibanserin with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379986, 'Fluconazole', 'Carbamazepine', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379987, 'Fludrocortisone', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', 'DDInter', 1767317475, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever carbamazepine is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379988, 'Fluoxetine', 'Carbamazepine', 'Moderate', 'Fluoxetine may inhibit the hepatic metabolism of carbamazepine. Carbamazepine toxicity is possible.', 'DDInter', 1767317475, 'Until more information is available, close observation for clinical and laboratory evidence of carbamazepine toxicity is recommended. Dose adjustments may be required. Patients should be advised to report possible symptoms of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) or serotonin syndrome (uncontrollable shivering, agitation, incoordination, restlessness, involuntary movements, hyperreflexia, and hyperarousal).', 'Metabolism', 'Until more information is available, close observation for clinical and laboratory evidence of carbamazepine toxicity is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379989, 'Fluphenazine', 'Carbamazepine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379990, 'Flurazepam', 'Carbamazepine', 'Moderate', 'Some antiepileptic agents such as carbamazepine and phenytoin significantly reduce the effect of oral midazolam. The mechanism may be due to enhanced gut and liver metabolism of midazolam (via CYP450 3A4 enzyme induction by carbamazepine). Parenteral midazolam is not likely to be affected. Other oral benzodiazepines metabolized by the 3A4 isoenzyme may interact similarly.', 'DDInter', 1767317475, 'Patients receiving this combination should be monitored for clinical response. Alternative oral sedative hypnotics may be preferable in patients receiving carbamazepine.', 'Metabolism', 'Patients receiving this combination should be monitored for clinical response.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379991, 'Ginkgo biloba', 'Carbamazepine', 'Moderate', 'Certain preparations of ginkgo biloba have been reported to induce seizures and may antagonize the effects of anticonvulsants. Ginkgo products may contain varying amounts of 4''-O-methylpyridoxine (ginkgotoxin), a known neurotoxin found primarily in ginkgo biloba seeds but also detected in lesser amounts in the leaves. In vivo, 4''-O-methylpyridoxine competes with vitamin B6, which causes an indirect inhibition of glutamate decarboxylase and subsequent decrease in the formation of gamma-aminobutyric acid (GABA) in the brain.', 'DDInter', 1767317475, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Because of inconsistencies in formulation and potency of commercial herbal preparations, there is no way to verify without laboratory testing if and in what quantity 4''-O-methylpyridoxine may be present in a given ginkgo preparation. Patients treated with anticonvulsants should preferably avoid the use of products containing ginkgo biloba.', 'Antagonism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379992, 'Glasdegib', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of glasdegib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of glasdegib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379993, 'Glycerin', 'Carbamazepine', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 1767317475, 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379994, 'Glycerol phenylbutyrate', 'Carbamazepine', 'Moderate', 'Drugs that can increase plasma ammonia levels such as corticosteroids, haloperidol, or the anticonvulsants carbamazepine, topiramate, and phenobarbital may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Corticosteroids can cause the breakdown of body protein, which may lead to increased ammonia levels. The use of haloperidol has been associated with hyperammonemia in a young child with citrullinemia, an inherited disorder of ammonia excretion.', 'DDInter', 1767317475, 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', 'Others', 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379995, 'Golimumab', 'Carbamazepine', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317475, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1379996, 'Grepafloxacin', 'Carbamazepine', 'Moderate', 'Based on in vitro inhibition data, coadministration with grepafloxacin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by grepafloxacin.', 'DDInter', 1767317475, 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever grepafloxacin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if grepafloxacin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
