INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380011, 'Imipramine', 'Carbamazepine', 'Moderate', 'Carbamazepine may decrease serum concentrations of tricyclic antidepressants. The mechanism may be related to carbamazepine induction of CYP450 enzymes responsible for tricyclic antidepressant metabolism. Serum concentrations of carbamazepine may be increased by an unknown mechanism. Additionally, tricyclic antidepressants may counteract the anticonvulsant effects of carbamazepine by lowering the seizure threshold.', 'DDInter', 1767317475, 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', 'Metabolism', 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
