INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380013, 'Indinavir', 'Carbamazepine', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380014, 'Infliximab', 'Carbamazepine', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317475, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380015, 'Interferon beta-1a', 'Carbamazepine', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380016, 'Interferon beta-1b', 'Carbamazepine', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380017, 'Irinotecan', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4 to inactive substances, and induction of this process results in less of the drug available for conversion to SN-38 via hepatic carboxylesterases. Available data also suggest induction of other enzymatic pathways (e.g., UGT1A1; carboxylesterases) and drug transporters (e.g., multispecific organic anion transporter, or MRP2; mitoxantrone-resistance half transporter protein, or MXR) that may be involved in the clearance of irinotecan and/or SN-38, although the extent to which they contribute to the interaction is unknown.', 'DDInter', 1767317475, 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided. Consideration should be given to substituting nonenzyme-inducing agents at least one to two weeks prior to initiation of irinotecan therapy whenever possible.', 'Metabolism', 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380018, 'Irinotecan (liposomal)', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4 to inactive substances, and induction of this process results in less of the drug available for conversion to SN-38 via hepatic carboxylesterases. Available data also suggest induction of other enzymatic pathways (e.g., UGT1A1; carboxylesterases) and drug transporters (e.g., multispecific organic anion transporter, or MRP2; mitoxantrone-resistance half transporter protein, or MXR) that may be involved in the clearance of irinotecan and/or SN-38, although the extent to which they contribute to the interaction is unknown.', 'DDInter', 1767317475, 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided. Consideration should be given to substituting nonenzyme-inducing agents at least one to two weeks prior to initiation of irinotecan therapy whenever possible.', 'Metabolism', 'Concomitant use of irinotecan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380019, 'Isavuconazonium', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317475, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380020, 'Isocarboxazid', 'Carbamazepine', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and dibenzazepine derivatives (e.g., tricyclic and tetracyclic antidepressants, cyclobenzaprine, carbamazepine) may produce significant adverse reactions including nausea, vomiting, flushing, dizziness, tremor, myoclonus, rigidity, diaphoresis, hyperthermia, autonomic instability, hypertensive crises, disseminated intravascular coagulation, severe convulsive seizures, coma, and death. The exact mechanism of interaction is unknown, but may involve excessive serotonergic activity in the central nervous system (i.e., serotonin syndrome).', 'DDInter', 1767317475, 'In general, dibenzazepine derivatives should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with tricyclic antidepressants, and vice versa.', 'Others', 'In general, dibenzazepine derivatives should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380021, 'Isoniazid', 'Carbamazepine', 'Moderate', 'Isoniazid increases serum carbamazepine levels in most patients. Also, carbamazepine may potentiate isoniazid hepatotoxicity. The mechanism is CYP450 3A4 inhibition by isoniazid.', 'DDInter', 1767317475, 'Close attention should be paid to clinical and laboratory evidence of hepatotoxicity and carbamazepine toxicity if patients must take isoniazid concurrently. Clinical observation of the patient is recommended, and carbamazepine dosage should be adjusted as indicated. Isoniazid should be discontinued if hepatotoxicity occurs. Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) or early signs of hepatotoxicity (fatigue, weakness, malaise, anorexia, nausea, or vomiting) to their caregiver.', 'Metabolism', 'Close attention should be paid to clinical and laboratory evidence of hepatotoxicity and carbamazepine toxicity if patients must take isoniazid concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380022, 'Isradipine', 'Carbamazepine', 'Major', 'Coadministration with carbamazepine may significantly decrease the plasma concentrations and pharmacologic effects of calcium channel blockers (CCBs), especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nimodipine, nisoldipine). The proposed mechanism is carbamazepine induction of CYP450-mediated metabolism.', 'DDInter', 1767317475, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers such as carbamazepine should generally be avoided. If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation, discontinuation or change of dosage of carbamazepine, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers such as carbamazepine should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380023, 'Istradefylline', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', 'DDInter', 1767317475, 'The use of istradefylline in combination with potent CYP450 3A4 inducers is not recommended. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.', 'Metabolism', 'The use of istradefylline in combination with potent CYP450 3A4 inducers is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380024, 'Itraconazole', 'Carbamazepine', 'Moderate', 'The coadministration with carbamazepine may result in decreased plasma concentrations of the azole antifungal agents, itraconazole and ketoconazole. The mechanism is accelerated azole clearance due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', 'DDInter', 1767317475, 'Because of these mutual effects, concomitant use of itraconazole or ketoconazole with carbamazepine is not recommended. In addition, some authorities recommend avoiding concomitant therapy from 2 weeks before, during, and for 2 weeks after treatment with itraconazole. Patients should be monitored for carbamazepine toxicity and antifungal treatment failure and dose adjustments may be required. Alternatively, other antifungal agents which are less likely to interact may be considered (e.g., terbinafine or fluconazole). Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) to their physicians.', 'Metabolism', 'Because of these mutual effects, concomitant use of itraconazole or ketoconazole with carbamazepine is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380025, 'Ivabradine', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ivabradine and its active N-desmethylated metabolite, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ivabradine with potent CYP450 3A4 inducers should generally be avoided. If coadministration is required, the possibility of diminished therapeutic response to ivabradine should be considered. Pharmacologic response to ivabradine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Concomitant use of ivabradine with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380026, 'Ivacaftor', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ivacaftor-containing medications with potent CYP450 3A4 inducers is not recommended.', 'Metabolism', 'Concomitant use of ivacaftor-containing medications with potent CYP450 3A4 inducers is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
