INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380059, 'Carbamazepine', 'Lithium carbonate', 'Moderate', 'The combination of lithium and carbamazepine, although generally well tolerated, has been reported to cause lethargy, muscular weakness, ataxia, dystonia, a tardive dyskinesia-like syndrome, tremor, sinus node dysfunction.', 'DDInter', 1767317475, 'Close monitoring for lithium toxicity, altered thyroxine levels, and adverse neurologic and cardiac effects. Patients should be advised to report possible neurotoxic symptoms (tremor, ataxia, hyperreflexia, nystagmus, confusion, drowsiness, or lethargy).', 'Others', 'Close monitoring for lithium toxicity, altered thyroxine levels, and adverse neurologic and cardiac effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380060, 'Carbamazepine', 'Lofexidine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380061, 'Carbamazepine', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', 'DDInter', 1767317475, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380062, 'Carbamazepine', 'Lonafarnib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', 'DDInter', 1767317475, 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Concomitant use of lonafarnib with potent or moderate CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380063, 'Carbamazepine', 'Lorazepam', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380064, 'Carbamazepine', 'Lorlatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. In addition to the potential for diminished efficacy of lorlatinib, the risk of serious hepatotoxicity may be increased due to activation of the pregnane X receptor (PXR) by both lorlatinib and CYP450 3A4 inducers.', 'DDInter', 1767317475, 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated. Lorlatinib should not be initiated until after discontinuation of potent CYP450 3A4 inducers for at least 3 plasma half-lives.', 'Metabolism', 'Concomitant use of lorlatinib with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
