INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380067, 'Carbamazepine', 'Lurasidone', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lurasidone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of lurasidone with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Concomitant use of lurasidone with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380068, 'Carbamazepine', 'Lurbinectedin', 'Major', 'Coadministration with a strong or moderate CYP450 3A4 inducer is predicted to decrease lurbinectedin systemic exposure, which may reduce its efficacy.', 'DDInter', 1767317475, 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380069, 'Carbamazepine', 'Macimorelin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration. A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin. No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380070, 'Carbamazepine', 'Macitentan', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of macitentan, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of macitentan with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of macitentan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380071, 'Carbamazepine', 'Mannitol', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 1767317475, 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380072, 'Carbamazepine', 'Maprotiline', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380073, 'Carbamazepine', 'Maraviroc', 'Major', 'Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc may have an increased risk of postural hypotension due to increased maraviroc exposure. Moreover, these patients often have cardiovascular comorbidities that could predispose them to adverse cardiovascular events triggered by postural hypotension. Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers. However, if a potent CYP450 3A4 inhibitor such as itraconazole, ketoconazole, delavirdine, clarithromycin, telithromycin, nefazodone, or any protease inhibitor (except tipranavir plus ritonavir) is also used in combination with the inducer, then maraviroc dosage should be reduced to 150 mg twice daily. Maraviroc is contraindicated for use with potent CYP450 3A4 inducers in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380074, 'Carbamazepine', 'Mebendazole', 'Moderate', 'Coadministration with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, and primidone may decrease mebendazole levels. The mechanism is unknown, but induction of hepatic metabolism by these anticonvulsant drugs has been proposed.', 'DDInter', 1767317475, 'Caution and monitoring for altered clinical efficacy are recommended if mebendazole is used concomitantly with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, or primidone in patients being treated for systemic helminthic infections. Dose adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Caution and monitoring for altered clinical efficacy are recommended if mebendazole is used concomitantly with anticonvulsant drugs such as carbamazepine, phenytoin, phenobarbital, or primidone in patients being treated for systemic helminthic infections.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380075, 'Carbamazepine', 'Meclizine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380076, 'Carbamazepine', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of medroxyprogesterone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary. When administered as the depot formulation for contraception, no dosage adjustment for medroxyprogesterone is currently recommended during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380077, 'Carbamazepine', 'Mefloquine', 'Moderate', 'Mefloquine may increase the risk of seizures and decrease the effectiveness of anticonvulsant drugs in patients with epilepsy. The mechanism is unknown, and it is unclear whether the seizures occur due to drug-drug or drug-disease interactions, as mefloquine alone and in combination with other related drugs (e.g., quinine, quinidine, chloroquine) have also been associated with seizures in some patients.', 'DDInter', 1767317475, 'Because it can increase the risk of seizures in patients with epilepsy, mefloquine should only be used for curative treatment of malaria in such patients and only if there are compelling medical reasons for its use. Patient clinical status and anticonvulsant blood levels should be closely monitored if mefloquine is prescribed. Dosage adjustments may be required. Patients should be advised to notify their doctor if they experience a loss of seizure control. Mefloquine should not be prescribed for malaria prophylaxis in patients with a history of convulsions.', 'Others', 'Because it can increase the risk of seizures in patients with epilepsy, mefloquine should only be used for curative treatment of malaria in such patients and only if there are compelling medical reasons for its use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380078, 'Carbamazepine', 'Megestrol acetate', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380079, 'Carbamazepine', 'Melatonin', 'Minor', 'Concomitant administration of CYP450 1A2 inducers may decrease the serum concentration of melatonin. The clinical significance is unknown.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380080, 'Carbamazepine', 'Meperidine', 'Moderate', 'The central nervous system and respiratory depressant effects of meperidine may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered, particularly in elderly or debilitated patients.', 'DDInter', 1767317475, 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol. A lower dosage of meperidine should be considered initially, then titrated carefully according to pain level and clinical response. Meperidine dosage reductions of 25% to 50% have been recommended for patients receiving phenothiazines and other tranquilizers.', 'Synergism', 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380081, 'Carbamazepine', 'Mephenytoin', 'Moderate', 'Hydantoins may decrease carbamazepine levels, and carbamazepine may have variable effects on hydantoin levels. The mechanism may be related to induction of CYP450 hepatic metabolism of carbamazepine and alteration of hydantoin metabolism.', 'DDInter', 1767317475, 'Close observation for clinical and laboratory evidence of altered effects is recommended, particularly when one drug is started or discontinued. Patients should be advised to notify their physician if they experience loss of seizure control or symptoms of hydantoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia).', 'Metabolism', 'Close observation for clinical and laboratory evidence of altered effects is recommended, particularly when one drug is started or discontinued.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380082, 'Carbamazepine', 'Methylphenobarbital', 'Minor', 'Coadministration with a barbiturate may decrease the plasma concentrations of carbamazepine and increase concentrations of its active 10,11-epoxide metabolite. The mechanism is barbiturate induction of CYP450 3A4, the isoenzyme responsible for the metabolism of carbamazepine to carbamazepine-10,11-epoxide (CBZ-E). Until more data are available, it may be reasonable to monitor serum carbamazepine levels more closely whenever a barbiturate (if used for more than a few days) is added to or withdrawn from therapy.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
