INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380086, 'Carbamazepine', 'Methadone', 'Major', 'Coadministration with inducers of various CYP450 isoenzymes may decrease the plasma concentrations of methadone, which is metabolized by CYP450 3A4, 2B6, 2C19, 2C9, and 2D6. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on methadone following the addition of an inducer. Conversely, discontinuation of the inducer may increase methadone plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317475, 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers. Pharmacologic response to methadone should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1380087, 'Carbamazepine', 'Methazolamide', 'Moderate', 'Coadministration with a carbonic anhydrase inhibitor may increase the serum concentration and the risk of toxicity of carbamazepine. The mechanism of this interaction has not been delineated.', 'DDInter', 1767317475, 'Clinical monitoring of carbamazepine serum concentrations is recommended and the dosage should be adjusted accordingly. Patients should be advised to report possible signs of carbamazepine toxicity, such as headache, nausea, dizziness, slurred speech, nystagmus, visual disturbances, tremors, and ataxia, to their caregiver.', 'Synergism', 'Clinical monitoring of carbamazepine serum concentrations is recommended and the dosage should be adjusted accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
