INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93590, 'Ioflupane I-123', 'Duloxetine', 'Moderate', 'Drugs that bind to the dopamine transporter with high affinity may interfere with the image obtained using ioflupane I-123, which also binds to the dopamine transporter. These drugs include amoxapine, amphetamine, benztropine, bupropion, buspirone, cocaine, diethylpropion, ketamine, mazindol, methamphetamine, methylphenidate, modafinil, pemoline, phencyclidine (PCP), phentermine, phenylpropanolamine, pipradrol, selegiline, sertraline, and trihexyphenidyl. Selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs) may increase or decrease ioflupane binding to the dopamine transporter. Whether discontinuation of these drugs prior to ioflupane I-123 administration may help to minimize potential interference is unknown.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', 'Others', 'Maprotiline, Vortioxetine, Milnacipran, Levomilnacipran, Doxepin, Isocarboxazid, Viloxazine, Imipramine, Esketamine, Phenelzine, Desipramine, More', 'Indium In-111 pentetate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93694/', '[1] "Product Information. DaTscan (ioflupane I-123)." GE Healthcare, Princeton, NJ.[2] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC, New Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93591, 'Iohexol', 'Duloxetine', 'Major', 'Intrathecal administration of iodinated contrast media may induce seizures. Although clinical data are generally lacking, there may be a theoretical risk of increased seizure potential when used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline). These agents are often individually epileptogenic and may have additive effects when combined.', NULL, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient.', NULL, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient. Otherwise, close monitoring is advised during and after contrast administration.', 'Synergism', 'St. John''s Wort, Oxitriptan, Viloxazine, Tryptophan, Esketamine', 'Ioxilan, Iodipamide, Ioversol, Iodamide, Iodixanol, Tyropanoic acid, Ethiodized oil, Iopanoic acid, Iopodic acid, Iothalamic acid, Ioxaglic acid, More', 'Major', NULL, 'DDInt