INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93719, 'Duloxetine', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Sertraline, Maprotiline, Fluoxetine, Levomilnacipran, Doxepin, Isocarboxazid, Amoxapine, Imipramine, Esketamine, Phenelzine, Desipramine, More', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Tepotinib, Larotrectinib, Bosutinib, Lapatinib, Selumetinib, Ribociclib, Vandetanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93823/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (93720, 'Duloxetine', 'Phenacemide', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'Antagonism